/**
 *页面元素清理
 *classlist[i]写要删除的class
 */
function class_clear() {
    const classlist=[
        "noVipDownBox",//非VIP下载按钮
        "welfareMod",
        "equityBanner",
        "vipPurchase",
        "vipTitle",
        "videoItem",
        "rightBox",
        "openVipCom youthVipCom",
        "openProCom jmloading",
        "goVip nologinGoVip",
        "mian-nav-list",
        "search el-input el-input--prefix el-input--suffix btn2",
    ]
    let class_remove_time = 0
    let class_remove = setInterval(function () {
        class_remove_time = class_remove_time + 1
        for (let i in classlist) {
            let c = document.getElementsByClassName(classlist[i])
            if(c && c[0]){
                c[0].remove();
            }else if(class_remove_time>300){
                clearInterval(class_remove)
                // console.log('停止清理界面');
            }
        }
        if(document.querySelector('.fileBox')){document.querySelector('.fileBox').style.width = '918px' }
        if(document.querySelector('.main-content')){document.querySelector('.main-content').style.backgroundImage = '' }
        if(document.querySelector('.main-body')){document.querySelector('.main-body').style.background = '#ffffff'; }
    },300);
}

